import React, {Component} from 'react';
import CardList from './CardList';
import {robots} from './robots';
import SearchBox from './SearchBox';
import './App.css'



class App extends Component {

    constructor(){
        super()
        this.state = {
            robo:[],
            searchfield : ''
        }
    }

    //because it's react, we don't have to use arrow function
    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/users').then(response =>{response.json();}).then(users=>{this.setState()})

    }

    //a madeup function that bridges together search box and robots
    onSearchChange = (event) => {
        this.setState({searchfield : event.target.value});

        console.log(event.target.value);

    }




    render(){
        const filterRobo = this.state.robo.filter(robot =>{
            return robot.name.toLocaleLowerCase().includes(this.state.searchfield.toLocaleLowerCase())
        });
        return(
            <div className='tc'>
            <h1 className='f1'>Robo Friends</h1>
            <SearchBox searchChange = {this.onSearchChange}/>
            <CardList robot={filterRobo} />
            </div>


        );

    }

}

export default App;
